// Removed TypeScript imports - using JavaScript
import { createServer } from "http";
import multer from "multer";
import * as XLSX from 'xlsx';
import csv from 'csv-parser';
import { Readable } from 'stream';
import { storage } from "./storage-mongodb";
import { validateAdminCredentials, changeAdminCredentials, validatePasswordStrength } from "./admin-credentials";
import { insertDonationSchema } from "@shared/mongodb-schema";

const requireAuth = (req: any, res: any, next: any) => {
  interface SessionData {
    isAuthenticated?: boolean;
    username?: string;
    role?: string;
  }

  const session = req.session as SessionData;

  if (!session.isAuthenticated) {
    return res.status(401).json({ error: "Authentication required" });
  }
  next();
};

const upload = multer({ 
  storage: multer.memoryStorage(),
  limits: { fileSize: 5 * 1024 * 1024 }, // 5MB limit
  fileFilter: (req, file, cb) => {
    const validMimeTypes = [
      'text/csv',
      'application/vnd.ms-excel',
      'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet'
    ];
    const validExtensions = ['.csv', '.xlsx', '.xls'];

    if (validMimeTypes.includes(file.mimetype) || 
        validExtensions.some(ext => file.originalname.toLowerCase().endsWith(ext))) {
      cb(null, true);
    } else {
      cb(new Error('Only CSV and Excel files are allowed'));
    }
  }
});

export async function registerMongoDBRoutes(app: any) {
  // Admin authentication routes (optimized for speed)
  app.post("/api/auth/login", async (req: any, res: any) => {
    const start = Date.now();
    try {
      const { username, password } = req.body;

      // Fast credential validation
      const admin = validateAdminCredentials(username, password);
      if (admin) {
        req.session.isAuthenticated = true;
        req.session.username = username;
        req.session.role = admin.role;

        // Save session immediately for faster response
        req.session.save((err: any) => {
          if (err) {
            console.error('Session save error:', err);
          }
        });

        const duration = Date.now() - start;
        console.log(`Login processed in ${duration}ms`);

        // Fast response with cache headers
        res.setHeader('Cache-Control', 'no-cache, no-store, must-revalidate');
        res.json({ 
          success: true, 
          message: "Login successful",
          role: admin.role 
        });
      } else {
        res.status(401).json({ error: "Invalid credentials" });
      }
    } catch (error) {
      console.error('Login error:', error);
      res.status(500).json({ error: "Login failed" });
    }
  });

  app.post("/api/auth/logout", (req: any, res: any) => {
    req.session.destroy((err: any) => {
      if (err) {
        return res.status(500).json({ error: "Logout failed" });
      }
      res.clearCookie('connect.sid');
      res.json({ success: true, message: "Logged out successfully" });
    });
  });

  app.get("/api/auth/status", (req: any, res: any) => {
    const session = req.session;

    // Fast auth status with caching for better performance
    res.setHeader('Cache-Control', 'private, max-age=5'); // 5 second cache
    res.json({
      isAuthenticated: !!session.isAuthenticated,
      username: session.username || null,
      role: session.role || null
    });
  });

  // Change admin credentials
  app.post("/api/auth/change-credentials", requireAuth, async (req: any, res: any) => {
    try {
      const { currentPassword, newUsername, newPassword, confirmPassword } = req.body;

      // Validate input
      if (!currentPassword || !newUsername || !newPassword || !confirmPassword) {
        return res.status(400).json({ error: "All fields are required" });
      }

      if (newPassword !== confirmPassword) {
        return res.status(400).json({ error: "New password and confirm password do not match" });
      }

      // Validate current password
      const currentUsername = req.session?.username;
      if (!currentUsername) {
        return res.status(401).json({ error: "Not authenticated" });
      }

      const admin = validateAdminCredentials(currentUsername, currentPassword);
      if (!admin) {
        return res.status(401).json({ error: "Current password is incorrect" });
      }

      // Validate new password strength
      const passwordValidation = validatePasswordStrength(newPassword);
      if (!passwordValidation.isValid) {
        return res.status(400).json({ 
          error: "Password does not meet security requirements",
          details: passwordValidation.errors
        });
      }

      // Change credentials
      const success = changeAdminCredentials(currentUsername, newUsername, newPassword);
      if (!success) {
        return res.status(500).json({ error: "Failed to update credentials" });
      }

      // Update session with new username
      req.session.username = newUsername;
      req.session.save((err: any) => {
        if (err) {
          console.error('Session save error after credential change:', err);
        }
      });

      res.json({ 
        success: true, 
        message: "Credentials updated successfully! Please use your new credentials for future logins."
      });
    } catch (error) {
      console.error('Change credentials error:', error);
      res.status(500).json({ 
        error: (error as Error).message || "Failed to change credentials"
      });
    }
  });

  // Export donations as CSV (must be before /:id route)
  app.get("/api/donations/export", requireAuth, async (req: any, res: any) => {
    try {
      const donations = await storage.getAllDonations();

      // Create CSV content
      const headers = ['S.No', 'Receipt No', 'Name', 'Community', 'Location', 'Address', 'Phone', 'Amount', 'Payment Mode', 'Inscription', 'Date'];
      const csvContent = [
        headers.join(','),
        ...donations.map((donation, index) => [
          index + 1,
          donation.receiptNo,
          `"${donation.name}"`,
          `"${donation.community || ''}"`,
          `"${donation.location || ''}"`,
          `"${(donation as any).address || ''}"`,
          donation.phone,
          donation.amount,
          donation.paymentMode,
          donation.inscription ? 'Yes' : 'No',
          new Date(donation.createdAt).toLocaleDateString('en-GB')
        ].join(','))
      ].join('\n');

      res.setHeader('Content-Type', 'text/csv');
      res.setHeader('Content-Disposition', 'attachment; filename=donations.csv');
      res.send(csvContent);
    } catch (error) {
      res.status(500).json({ message: "Failed to export donations" });
    }
  });

  // Import donations from CSV or Excel (protected admin route)
  app.post("/api/donations/import", requireAuth, upload.single('file'), async (req: any, res: any) => {
    try {
      if (!req.file) {
        return res.status(400).json({ 
          success: false, 
          message: "No file uploaded" 
        });
      }

      const fileName = req.file.originalname.toLowerCase();
      let records: any[] = [];

      // Parse CSV files
      if (fileName.endsWith('.csv')) {
        const csvString = req.file.buffer.toString('utf-8');
        const lines = csvString.split('\n').filter((line: string) => line.trim());

        if (lines.length < 2) {
          return res.status(400).json({
            success: false,
            message: "CSV file must contain headers and at least one data row"
          });
        }

        const headers = lines[0].split(',').map((h: string) => h.replace(/"/g, '').trim());
        console.log('CSV Headers found:', headers);

        for (let i = 1; i < lines.length; i++) {
          const values = lines[i].split(',').map((v: string) => v.replace(/"/g, '').trim());
          if (values.every((v: string) => !v)) continue;

          const record: any = {};
          headers.forEach((header: string, index: number) => {
            const cleanHeader = header.replace(/["\s\.]/g, '').toLowerCase();
            record[cleanHeader] = values[index] || '';
          });

          records.push(record);
        }
      }
      // Parse Excel files
      else if (fileName.endsWith('.xlsx') || fileName.endsWith('.xls')) {
        const workbook = XLSX.read(req.file.buffer, { type: 'buffer' });
        const sheetName = workbook.SheetNames[0];
        const worksheet = workbook.Sheets[sheetName];
        const jsonData = XLSX.utils.sheet_to_json(worksheet, { header: 1 });

        if (jsonData.length < 2) {
          return res.status(400).json({
            success: false,
            message: "Excel file must contain headers and at least one data row"
          });
        }

        const headers = (jsonData[0] as any[]).map(h => String(h || '').trim());
        console.log('Excel Headers found:', headers);

        // Process each Excel data row
        for (let i = 1; i < jsonData.length; i++) {
          const row = jsonData[i] as any[];
          if (!row || row.every(cell => !cell)) continue;

          const record: any = {};
          headers.forEach((header: string, index: number) => {
            const cleanHeader = header.replace(/["\s\.]/g, '').toLowerCase();
            record[cleanHeader] = String(row[index] || '').trim();
          });

          records.push(record);
        }
      } else {
        return res.status(400).json({
          success: false,
          message: "Unsupported file format. Please upload CSV or Excel files."
        });
      }

      console.log(`Processing ${records.length} records from ${fileName}`);

      // Process and validate each record
      let successCount = 0;
      let failureCount = 0;
      const errors: string[] = [];

      for (const [index, record] of records.entries()) {
        try {
          // Map flexible field names
          const donation: any = {
            receiptNo: record.receiptno || record.receipt || record.receiptNumber || '',
            name: record.name || record.donorname || '',
            phone: record.phone || record.phonenumber || record.mobile || '',
            community: record.community || record.kulam || record.caste || 'any',
            location: record.location || record.place || record.city || '',
            address: record.address || record.fulladdress || '',
            amount: 0,
            paymentMode: 'cash',
            inscription: false,
            donationDate: null
          };

          // Validate and convert amount
          const amountStr = String(record.amount || record.donationamount || '0').replace(/[â‚¹,]/g, '');
          donation.amount = parseFloat(amountStr) || 0;

          if (!donation.receiptNo || !donation.name || !donation.phone || donation.amount <= 0) {
            errors.push(`Row ${index + 1}: Missing required fields (Receipt No, Name, Phone, Amount)`);
            failureCount++;
            continue;
          }

          // Validate phone number
          const phoneStr = String(donation.phone).replace(/\D/g, '');
          if (phoneStr.length !== 10) {
            errors.push(`Row ${index + 1}: Phone number must be exactly 10 digits, found: ${donation.phone}`);
            failureCount++;
            continue;
          }
          donation.phone = phoneStr;

          // Normalize community
          const communityMap: { [key: string]: string } = {
            'any': 'any', 'payiran': 'payiran', 'chozhan': 'chozhan', 
            'pandiyan': 'pandiyan', 'othaalan': 'othaalan', 'vizhiyan': 'vizhiyan',
            'aadai': 'aadai', 'aavan': 'aavan', 'odhaalan': 'odhaalan', 'semban': 'semban'
          };

          const normalizedCommunity = communityMap[donation.community.toLowerCase()] || 'any';
          donation.community = normalizedCommunity;

          // Normalize payment mode
          const paymentModeMap: { [key: string]: string } = {
            'cash': 'cash', 'card': 'card', 'upi': 'upi', 
            'banktransfer': 'bankTransfer', 'bank': 'bankTransfer',
            'cheque': 'cheque', 'check': 'cheque'
          };

          const paymentModeStr = String(record.paymentmode || record.payment || 'cash').toLowerCase().replace(/\s/g, '');
          donation.paymentMode = paymentModeMap[paymentModeStr] || 'cash';

          // Handle inscription
          const inscriptionStr = String(record.inscription || record.engraving || 'no').toLowerCase();
          donation.inscription = inscriptionStr === 'yes' || inscriptionStr === 'true' || inscriptionStr === '1';

          // Parse date if provided - Enhanced date handling
          if (record.date || record.donationdate) {
            const dateStr = String(record.date || record.donationdate).trim();
            console.log(`Processing date: "${dateStr}" for row ${index + 1}`);

            if (dateStr && dateStr !== '' && dateStr !== 'undefined' && dateStr !== 'null') {
              // Handle Excel serial date numbers
              if (/^\d+(\.\d+)?$/.test(dateStr)) {
                const excelDate = parseFloat(dateStr);
                if (excelDate > 25569) { // Valid Excel date (after 1900)
                  const jsDate = new Date((excelDate - 25569) * 86400 * 1000);
                  donation.donationDate = jsDate;
                  console.log(`Converted Excel serial ${excelDate} to date: ${jsDate.toISOString()}`);
                }
              } else {
                // Handle multiple date formats
                let parsedDate = null;

                // Parse date with intelligent format detection
                const dateRegex = /^(\d{1,2})[\/\-](\d{1,2})[\/\-](\d{4})$/;
                const dateMatch = dateStr.match(dateRegex);
                if (dateMatch) {
                  const [, first, second, year] = dateMatch;
                  const firstNum = parseInt(first);
                  const secondNum = parseInt(second);
                  const yearNum = parseInt(year);

                  let day, month;

                  // Smart detection: If first number > 12, it must be day (DD/MM format)
                  if (firstNum > 12) {
                    day = firstNum;
                    month = secondNum;
                    console.log(`Detected DD/MM format: ${day}/${month}/${yearNum}`);
                  }
                  // If second number > 12, it must be day (MM/DD format)
                  else if (secondNum > 12) {
                    day = secondNum;
                    month = firstNum;
                    console.log(`Detected MM/DD format: ${month}/${day}/${yearNum}`);
                  }
                  // Both numbers <= 12: Use DD/MM as default for Indian format
                  else {
                    day = firstNum;
                    month = secondNum;
                    console.log(`Using DD/MM default: ${day}/${month}/${yearNum}`);
                  }

                  // Validate day and month ranges
                  if (day >= 1 && day <= 31 && month >= 1 && month <= 12) {
                    parsedDate = new Date(yearNum, month - 1, day);
                    console.log(`Final parsed date: Day=${day}, Month=${month}, Year=${yearNum} -> ${parsedDate.toISOString()}`);
                  } else {
                    console.log(`Invalid date values: Day=${day}, Month=${month}, Year=${yearNum}`);
                  }
                }

                // Try YYYY-MM-DD format as secondary option
                if (!parsedDate || isNaN(parsedDate.getTime())) {
                  const yyyymmddRegex = /^(\d{4})[\/\-](\d{1,2})[\/\-](\d{1,2})$/;
                  const yyyymmddMatch = dateStr.match(yyyymmddRegex);
                  if (yyyymmddMatch) {
                    const [, year, month, day] = yyyymmddMatch;
                    parsedDate = new Date(parseInt(year), parseInt(month) - 1, parseInt(day));
                    console.log(`Parsed as YYYY-MM-DD: ${year}/${month}/${day} -> ${parsedDate.toISOString()}`);
                  }
                }



                if (parsedDate && !isNaN(parsedDate.getTime())) {
                  donation.donationDate = parsedDate;
                  console.log(`Parsed date "${dateStr}" to: ${parsedDate.toISOString()}`);
                } else {
                  console.log(`Failed to parse date: "${dateStr}" for row ${index + 1}`);
                }
              }
            }
          }

          // Create donation in MongoDB
          await storage.createDonation(donation);
          successCount++;

        } catch (error: any) {
          console.error(`Error processing row ${index + 1}:`, error);
          if (error.message.includes('already exists')) {
            errors.push(`Row ${index + 1}: Receipt number ${record.receiptno || 'unknown'} is duplicate`);
          } else {
            errors.push(`Row ${index + 1}: ${error.message}`);
          }
          failureCount++;
        }
      }

      res.json({
        success: successCount > 0,
        totalRecords: records.length,
        successCount,
        failureCount,
        errors: errors.slice(0, 20)
      });

    } catch (error: any) {
      console.error('Import error:', error);
      res.status(500).json({ 
        success: false, 
        message: "Failed to import data: " + error.message,
        errors: [error.message]
      });
    }
  });

  // Get all donations with optional filters
  app.get("/api/donations", async (req: any, res: any) => {
    try {
      const { dateRange, community, paymentMode, amountRange } = req.query;

      const donations = await storage.getDonationsByFilters({
        dateRange: dateRange as string,
        community: community as string,
        paymentMode: paymentMode as string,
        amountRange: amountRange as string,
      });

      res.json(donations);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch donations" });
    }
  });

  // Check if receipt number exists (for duplicate validation)
  app.get("/api/donations/check-receipt/:receiptNo", async (req: any, res: any) => {
    try {
      const receiptNo = req.params.receiptNo;
      const donations = await storage.getAllDonations();
      const exists = donations.some(donation => donation.receiptNo === receiptNo);
      res.json({ exists });
    } catch (error) {
      res.status(500).json({ message: "Failed to check receipt number" });
    }
  });

  // Get donation by ID
  app.get("/api/donations/:id", async (req: any, res: any) => {
    try {
      const donation = await storage.getDonationById(req.params.id);
      if (!donation) {
        return res.status(404).json({ message: "Donation not found" });
      }
      res.json(donation);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch donation" });
    }
  });

  // Create new donation
  app.post("/api/donations", async (req: any, res: any) => {
    try {
      const donation = await storage.createDonation(req.body);
      res.status(201).json(donation);
    } catch (error: any) {
      console.error('Create donation error:', error);
      res.status(400).json({ message: error.message || "Failed to create donation" });
    }
  });

  // Update donation
  app.put("/api/donations/:id", requireAuth, async (req: any, res: any) => {
    try {
      const donation = await storage.updateDonation(req.params.id, req.body);
      if (!donation) {
        return res.status(404).json({ message: "Donation not found" });
      }
      res.json(donation);
    } catch (error) {
      res.status(400).json({ message: "Failed to update donation" });
    }
  });

  // Delete all donations (protected admin route) - MUST come before the :id route
  app.delete("/api/donations/all", requireAuth, async (req: any, res: any) => {
    try {
      const success = await storage.deleteAllDonations();

      if (success) {
        res.json({ message: "All donations deleted successfully" });
      } else {
        res.status(500).json({ message: "Failed to delete all donations" });
      }
    } catch (error) {
      res.status(500).json({ message: "Failed to delete all donations" });
    }
  });

  // Delete donation
  app.delete("/api/donations/:id", requireAuth, async (req: any, res: any) => {
    try {
      const success = await storage.deleteDonation(req.params.id);
      if (!success) {
        return res.status(404).json({ message: "Donation not found" });
      }
      res.json({ message: "Donation deleted successfully" });
    } catch (error) {
      res.status(500).json({ message: "Failed to delete donation" });
    }
  });

  // Donor search routes
  app.get("/api/donors/search", async (req: any, res: any) => {
    try {
      const { query, community } = req.query;
      if (!query || typeof query !== 'string') {
        return res.status(400).json({ message: "Search query is required" });
      }

      const donors = await storage.searchDonors(query, community as string);
      res.json(donors);
    } catch (error) {
      res.status(500).json({ message: "Failed to search donors" });
    }
  });

  app.get("/api/donors/:phone", async (req: any, res: any) => {
    try {
      const donor = await storage.getDonorByPhone(req.params.phone);
      if (!donor) {
        return res.status(404).json({ message: "Donor not found" });
      }
      res.json(donor);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch donor" });
    }
  });

  // Dashboard stats (optimized for fast loading)
  app.get("/api/dashboard/stats", requireAuth, async (req: any, res: any) => {
    const start = Date.now();
    try {
      const { dateRange, startDate, endDate } = req.query;
      const stats = await storage.getDashboardStats({
        dateRange: dateRange as string,
        startDate: startDate as string,
        endDate: endDate as string
      });
      const duration = Date.now() - start;
      console.log(`Dashboard stats loaded in ${duration}ms`);

      // Add aggressive cache headers for fastest loading
      res.setHeader('Cache-Control', 'public, max-age=30, stale-while-revalidate=60');
      res.setHeader('ETag', `"dashboard-${Date.now()}"`);
      res.json(stats);
    } catch (error) {
      console.error('Dashboard stats error:', error);
      res.status(500).json({ message: "Failed to fetch dashboard stats" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

