import { Donation, IDonation } from './models/Donation';
import { ReceiptSequence } from './models/ReceiptSequence';
import { connectMongoDB } from './mongodb';
import { dashboardCache } from './cache';

// Types matching the shared schema
export interface InsertDonation {
  receiptNo: string;
  name: string;
  phone: string;
  community: string;
  location: string;
  address?: string;
  amount: number;
  paymentMode: 'cash' | 'card' | 'upi' | 'bankTransfer' | 'cheque';
  inscription: boolean;
  donationDate?: Date;
}

export interface DonorSummary {
  name: string;
  phone: string;
  location: string;
  community: string;
  totalAmount: number;
  donationCount: number;
  lastDonation: string;
  donations: IDonation[];
}

export interface DashboardStats {
  totalCollection: number;
  totalCollections: number; // Legacy support
  totalDonors: number;
  totalDonations: number;
  thisMonth: number;
  avgDonation: number;
  paymentModeDistribution: {
    mode: string;
    percentage: number;
    amount: number;
  }[];
  recentDonations: {
    name: string;
    amount: number;
    paymentMode: string;
    createdAt: string;
  }[];
}

export interface IStorage {
  // Donation CRUD operations
  createDonation(donation: InsertDonation): Promise<IDonation>;
  getDonationById(id: string): Promise<IDonation | null>;
  getAllDonations(): Promise<IDonation[]>;
  getDonationsByFilters(filters: {
    dateRange?: string;
    community?: string;
    paymentMode?: string;
    amountRange?: string;
  }): Promise<IDonation[]>;
  updateDonation(id: string, donation: InsertDonation): Promise<IDonation | null>;
  deleteDonation(id: string): Promise<boolean>;
  deleteAllDonations(): Promise<boolean>;

  // Donor operations
  getDonorByPhone(phone: string): Promise<DonorSummary | null>;
  searchDonors(query: string, community?: string): Promise<DonorSummary[]>;

  // Dashboard operations
  getDashboardStats(filters?: {
    dateRange?: string;
    startDate?: string;
    endDate?: string;
  }): Promise<DashboardStats>;
}

// In-memory storage for development when MongoDB fails
class InMemoryStorage implements IStorage {
  private donations: any[] = [];
  private nextId: number = 1;

  async createDonation(insertDonation: InsertDonation): Promise<any> {
    // Check for duplicate receipt number
    const existingDonation = this.donations.find(d => d.receiptNo === insertDonation.receiptNo);
    if (existingDonation) {
      throw new Error(`Receipt number ${insertDonation.receiptNo} already exists`);
    }

    const donation = {
      _id: this.nextId.toString(),
      ...insertDonation,
      createdAt: new Date(),
      updatedAt: new Date()
    };
    this.donations.push(donation);
    this.nextId++;
    return donation;
  }

  async getDonationById(id: string): Promise<any> {
    return this.donations.find(d => d._id === id) || null;
  }

  async getAllDonations(): Promise<any[]> {
    return [...this.donations].sort((a, b) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime());
  }

  async getDonationsByFilters(filters: any): Promise<any[]> {
    let filtered = [...this.donations];

    if (filters.community && filters.community !== 'all') {
      filtered = filtered.filter(d => d.community === filters.community);
    }

    if (filters.paymentMode && filters.paymentMode !== 'all') {
      filtered = filtered.filter(d => d.paymentMode === filters.paymentMode);
    }

    return filtered.sort((a, b) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime());
  }

  async updateDonation(id: string, donation: any): Promise<any> {
    const index = this.donations.findIndex(d => d._id === id);
    if (index >= 0) {
      this.donations[index] = { ...this.donations[index], ...donation, updatedAt: new Date() };
      return this.donations[index];
    }
    return null;
  }

  async deleteDonation(id: string): Promise<boolean> {
    const index = this.donations.findIndex(d => d._id === id);
    if (index >= 0) {
      this.donations.splice(index, 1);
      return true;
    }
    return false;
  }

  async deleteAllDonations(): Promise<boolean> {
    this.donations = [];
    this.nextId = 1;
    return true;
  }

  async getDonorByPhone(phone: string): Promise<DonorSummary | null> {
    const donations = this.donations.filter(d => d.phone === phone);
    if (donations.length === 0) return null;

    const totalAmount = donations.reduce((sum, d) => sum + d.amount, 0);
    return {
      phone,
      name: donations[0].name,
      community: donations[0].community,
      location: donations[0].location,
      totalAmount,
      donationCount: donations.length,
      lastDonation: donations[0].createdAt.toISOString(),
      donations
    };
  }

  async searchDonors(query: string, community?: string): Promise<DonorSummary[]> {
    let filtered = [...this.donations];

    if (community && community !== 'all') {
      filtered = filtered.filter(d => d.community === community);
    }

    // Group by phone
    const donors: any = {};
    filtered.forEach(d => {
      if (!donors[d.phone]) {
        donors[d.phone] = {
          phone: d.phone,
          name: d.name,
          community: d.community,
          location: d.location,
          donations: [],
          totalAmount: 0,
          donationCount: 0,
          lastDonation: d.createdAt.toISOString()
        };
      }
      donors[d.phone].donations.push(d);
      donors[d.phone].totalAmount += d.amount;
      donors[d.phone].donationCount++;
    });

    // Filter by search query
    return Object.values(donors).filter((donor: any) => 
      donor.name.toLowerCase().includes(query.toLowerCase()) ||
      donor.phone.includes(query)
    ) as DonorSummary[];
  }

  async getDashboardStats(filters: any = {}): Promise<DashboardStats> {
    const { dateRange, startDate, endDate } = filters;

    // Filter donations based on date range
    let filteredDonations = [...this.donations];
    const now = new Date();

    if (dateRange && dateRange !== 'all') {
      switch (dateRange) {
        case 'thismonth': {
          const firstOfMonth = new Date(now.getFullYear(), now.getMonth(), 1);
          const lastOfMonth = new Date(now.getFullYear(), now.getMonth() + 1, 0);
          filteredDonations = this.donations.filter(d => {
            const donationDate = new Date(d.donationDate || d.createdAt);
            return donationDate >= firstOfMonth && donationDate <= lastOfMonth;
          });
          break;
        }
        case 'lastmonth': {
          const firstOfLastMonth = new Date(now.getFullYear(), now.getMonth() - 1, 1);
          const lastOfLastMonth = new Date(now.getFullYear(), now.getMonth(), 0);
          filteredDonations = this.donations.filter(d => {
            const donationDate = new Date(d.donationDate || d.createdAt);
            return donationDate >= firstOfLastMonth && donationDate <= lastOfLastMonth;
          });
          break;
        }
        case 'thisyear': {
          const firstOfYear = new Date(now.getFullYear(), 0, 1);
          const lastOfYear = new Date(now.getFullYear(), 11, 31);
          filteredDonations = this.donations.filter(d => {
            const donationDate = new Date(d.donationDate || d.createdAt);
            return donationDate >= firstOfYear && donationDate <= lastOfYear;
          });
          break;
        }
        case 'lastyear': {
          const firstOfLastYear = new Date(now.getFullYear() - 1, 0, 1);
          const lastOfLastYear = new Date(now.getFullYear() - 1, 11, 31);
          filteredDonations = this.donations.filter(d => {
            const donationDate = new Date(d.donationDate || d.createdAt);
            return donationDate >= firstOfLastYear && donationDate <= lastOfLastYear;
          });
          break;
        }
        case 'lastyear': {
          const firstOfLastYear = new Date(now.getFullYear() - 1, 0, 1);
          const lastOfLastYear = new Date(now.getFullYear() - 1, 11, 31);
          filteredDonations = this.donations.filter(d => {
            const donationDate = new Date(d.donationDate || d.createdAt);
            return donationDate >= firstOfLastYear && donationDate <= lastOfLastYear;
          });
          break;
        }
        case 'custom': {
          if (startDate && endDate) {
            const start = new Date(startDate);
            const end = new Date(endDate);
            filteredDonations = this.donations.filter(d => {
              const donationDate = new Date(d.donationDate || d.createdAt);
              return donationDate >= start && donationDate <= end;
            });
          }
          break;
        }
      }
    }

    // Always show total collection from all donations (not filtered)
    const totalCollections = this.donations.reduce((sum, d) => sum + d.amount, 0);
    const filteredTotal = filteredDonations.reduce((sum, d) => sum + d.amount, 0);
    const uniqueDonors = new Set(filteredDonations.map(d => d.phone)).size;

    // Calculate this month donations (always current month, not filtered)
    const firstOfMonth = new Date(now.getFullYear(), now.getMonth(), 1);
    const thisMonthDonations = this.donations.filter(d => 
      new Date(d.createdAt) >= firstOfMonth
    );
    const thisMonthAmount = thisMonthDonations.reduce((sum, d) => sum + d.amount, 0);

    // Payment mode distribution based on filtered data
    const paymentModes = ['cash', 'card', 'upi', 'bankTransfer', 'cheque'];
    const paymentModeDistribution = paymentModes.map(mode => {
      const modeAmount = filteredDonations
        .filter(d => d.paymentMode === mode)
        .reduce((sum, d) => sum + d.amount, 0);
      const count = filteredDonations.filter(d => d.paymentMode === mode).length;

      return {
        mode,
        count,
        percentage: filteredTotal > 0 ? Math.round((modeAmount / filteredTotal) * 100) : 0,
        amount: modeAmount
      };
    });

    return {
      totalCollection: totalCollections, // Always show total from all donations
      totalCollections: totalCollections, // Legacy support
      totalDonors: uniqueDonors,
      totalDonations: filteredDonations.length,
      thisMonth: thisMonthAmount,
      avgDonation: filteredDonations.length > 0 ? Math.round(filteredTotal / filteredDonations.length) : 0,
      paymentModeDistribution,
      recentDonations: filteredDonations.slice(-10).reverse().map(d => ({
        name: d.name,
        amount: d.amount,
        paymentMode: d.paymentMode,
        createdAt: d.createdAt.toISOString()
      }))
    };
  }
}

export class MongoDBStorage implements IStorage {
  private isMongoAvailable: boolean = false;
  private inMemoryStorage: InMemoryStorage;

  constructor() {
    this.isMongoAvailable = false;
    this.inMemoryStorage = new InMemoryStorage();

    // Try to connect to MongoDB, but don't wait for it
    this.initializeMongoDB();
  }

  async initializeMongoDB() {
    try {
      await connectMongoDB();
      this.isMongoAvailable = true;
      console.log('MongoDB connection established for storage');
    } catch (error) {
      console.log('MongoDB unavailable, using in-memory storage');
      this.isMongoAvailable = false;
    }
  }

  async createDonation(insertDonation: InsertDonation): Promise<IDonation> {
    // Use in-memory storage if MongoDB is not available
    if (!this.isMongoAvailable) {
      return await this.inMemoryStorage.createDonation(insertDonation);
    }

    try {
      // Check for duplicate receipt number in MongoDB
      const existingDonation = await Donation.findOne({ receiptNo: insertDonation.receiptNo });
      if (existingDonation) {
        throw new Error(`Receipt number ${insertDonation.receiptNo} already exists`);
      }

      const donation = new Donation(insertDonation);
      return await donation.save();
    } catch (error) {
      console.error('Error creating donation, falling back to in-memory:', error);
      this.isMongoAvailable = false;
      return await this.inMemoryStorage.createDonation(insertDonation);
    }
  }

  async getDonationById(id: string): Promise<IDonation | null> {
    if (!this.isMongoAvailable) {
      return await this.inMemoryStorage.getDonationById(id);
    }

    try {
      return await Donation.findById(id);
    } catch (error) {
      console.error('Error fetching donation by ID, falling back:', error);
      this.isMongoAvailable = false;
      return await this.inMemoryStorage.getDonationById(id);
    }
  }

  async getAllDonations(): Promise<IDonation[]> {
    if (!this.isMongoAvailable) {
      return await this.inMemoryStorage.getAllDonations();
    }

    try {
      return await Donation.find({}).sort({ createdAt: -1 });
    } catch (error) {
      console.error('Error fetching all donations, falling back:', error);
      this.isMongoAvailable = false;
      return await this.inMemoryStorage.getAllDonations();
    }
  }

  async getDonationsByFilters(filters: any): Promise<IDonation[]> {
    if (!this.isMongoAvailable) {
      return await this.inMemoryStorage.getDonationsByFilters(filters);
    }
    try {
      const query: any = {};

      // Date range filter
      if (filters.dateRange) {
        const cutoffDate = this.getDateRangeCutoff(filters.dateRange);
        if (cutoffDate) {
          query.createdAt = { $gte: cutoffDate };
        }
      }

      // Community filter
      if (filters.community && filters.community !== 'all') {
        query.community = filters.community;
      }

      // Payment mode filter
      if (filters.paymentMode && filters.paymentMode !== 'all') {
        query.paymentMode = filters.paymentMode;
      }

      // Amount range filter
      if (filters.amountRange && filters.amountRange !== 'all') {
        const [min, max] = this.parseAmountRange(filters.amountRange);
        if (min !== undefined && max !== undefined) {
          query.amount = { $gte: min, $lte: max };
        } else if (min !== undefined) {
          query.amount = { $gte: min };
        }
      }

      return await Donation.find(query).sort({ createdAt: -1 });
    } catch (error) {
      console.error('Error fetching donations with filters, falling back:', error);
      this.isMongoAvailable = false;
      return await this.inMemoryStorage.getDonationsByFilters(filters);
    }
  }

  private parseAmountRange(range: string): [number | undefined, number | undefined] {
    switch (range) {
      case '0-100': return [0, 100];
      case '101-500': return [101, 500];
      case '501-1000': return [501, 1000];
      case '1001-5000': return [1001, 5000];
      case '5000+': return [5000, undefined];
      default: return [undefined, undefined];
    }
  }

  private getDateRangeCutoff(range: string): Date | null {
    const now = new Date();
    switch (range) {
      case 'today':
        return new Date(now.getFullYear(), now.getMonth(), now.getDate());
      case 'week':
        const weekAgo = new Date(now);
        weekAgo.setDate(now.getDate() - 7);
        return weekAgo;
      case 'month':
        const monthAgo = new Date(now);
        monthAgo.setMonth(now.getMonth() - 1);
        return monthAgo;
      case 'year':
        const yearAgo = new Date(now);
        yearAgo.setFullYear(now.getFullYear() - 1);
        return yearAgo;
      default:
        return null;
    }
  }

  async getDonorByPhone(phone: string): Promise<DonorSummary | null> {
    if (!this.isMongoAvailable) {
      return await this.inMemoryStorage.getDonorByPhone(phone);
    }

    try {
      const donations = await Donation.find({ phone }).sort({ createdAt: -1 });

      if (donations.length === 0) {
        return null;
      }

      const firstDonation = donations[0];
      const totalAmount = donations.reduce((sum, d) => sum + d.amount, 0);

      return {
        name: firstDonation.name,
        phone: firstDonation.phone,
        location: firstDonation.location,
        community: firstDonation.community,
        totalAmount,
        donationCount: donations.length,
        lastDonation: firstDonation.createdAt.toISOString(),
        donations
      };
    } catch (error) {
      console.error('Error fetching donor by phone, falling back:', error);
      this.isMongoAvailable = false;
      return await this.inMemoryStorage.getDonorByPhone(phone);
    }
  }

  async searchDonors(query: string, community?: string): Promise<DonorSummary[]> {
    if (!this.isMongoAvailable) {
      return await this.inMemoryStorage.searchDonors(query, community);
    }

    try {
      const searchQuery = {
        $or: [
          { name: { $regex: query, $options: 'i' } },
          { phone: { $regex: query, $options: 'i' } }
        ]
      };

      if (community && community !== 'all') {
        (searchQuery as any).community = community;
      }

      const donations = await Donation.find(searchQuery).sort({ createdAt: -1 });

      // Group by phone number to get unique donors  
      const donorMap = new Map<string, DonorSummary>();

      for (const donation of donations) {
        if (!donorMap.has(donation.phone)) {
          const donorDonations = donations.filter(d => d.phone === donation.phone);
          const totalAmount = donorDonations.reduce((sum, d) => sum + d.amount, 0);

          donorMap.set(donation.phone, {
            name: donation.name,
            phone: donation.phone,
            location: donation.location,
            community: donation.community,
            totalAmount,
            donationCount: donorDonations.length,
            lastDonation: donation.createdAt.toISOString(),
            donations: donorDonations
          });
        }
      }

      return Array.from(donorMap.values());
    } catch (error) {
      console.error('Error searching donors, falling back:', error);
      this.isMongoAvailable = false;
      return await this.inMemoryStorage.searchDonors(query, community);
    }
  }

  async getDashboardStats(filters: any = {}): Promise<DashboardStats> {
    if (!this.isMongoAvailable) {
      return await this.inMemoryStorage.getDashboardStats(filters);
    }

    try {
      const { dateRange, startDate, endDate } = filters;

      // Create cache key based on filters
      const cacheKey = `dashboard-stats-${dateRange || 'all'}-${startDate || ''}-${endDate || ''}`;
      const cached = dashboardCache.get(cacheKey);
      if (cached) {
        console.log('Dashboard stats served from cache');
        return cached;
      }

      // Build MongoDB query based on date range
      let dateFilter = {};
      const now = new Date();

      if (dateRange && dateRange !== 'all') {
        switch (dateRange) {
          case 'thismonth': {
            const firstOfMonth = new Date(now.getFullYear(), now.getMonth(), 1);
            const lastOfMonth = new Date(now.getFullYear(), now.getMonth() + 1, 0);
            dateFilter = { 
              $or: [
                { donationDate: { $gte: firstOfMonth, $lte: lastOfMonth } },
                { donationDate: { $exists: false }, createdAt: { $gte: firstOfMonth, $lte: lastOfMonth } }
              ]
            };
            break;
          }
          case 'lastmonth': {
            const firstOfLastMonth = new Date(now.getFullYear(), now.getMonth() - 1, 1);
            const lastOfLastMonth = new Date(now.getFullYear(), now.getMonth(), 0);
            dateFilter = { 
              $or: [
                { donationDate: { $gte: firstOfLastMonth, $lte: lastOfLastMonth } },
                { donationDate: { $exists: false }, createdAt: { $gte: firstOfLastMonth, $lte: lastOfLastMonth } }
              ]
            };
            break;
          }
          case 'thisyear': {
            const firstOfYear = new Date(now.getFullYear(), 0, 1);
            const lastOfYear = new Date(now.getFullYear(), 11, 31);
            dateFilter = { 
              $or: [
                { donationDate: { $gte: firstOfYear, $lte: lastOfYear } },
                { donationDate: { $exists: false }, createdAt: { $gte: firstOfYear, $lte: lastOfYear } }
              ]
            };
            break;
          }
          case 'lastyear': {
            const firstOfLastYear = new Date(now.getFullYear() - 1, 0, 1);
            const lastOfLastYear = new Date(now.getFullYear() - 1, 11, 31);
            dateFilter = { 
              $or: [
                { donationDate: { $gte: firstOfLastYear, $lte: lastOfLastYear } },
                { donationDate: { $exists: false }, createdAt: { $gte: firstOfLastYear, $lte: lastOfLastYear } }
              ]
            };
            break;
          }
          case 'custom': {
            if (startDate && endDate) {
              dateFilter = { 
                $or: [
                  { donationDate: { $gte: new Date(startDate), $lte: new Date(endDate) } },
                  { donationDate: { $exists: false }, createdAt: { $gte: new Date(startDate), $lte: new Date(endDate) } }
                ]
              };
            }
            break;
          }
        }
      }

      // Get filtered donations
      const filteredDonations = await Donation.find(dateFilter);

      // Always get this month data (unfiltered)
      const firstOfMonth = new Date(now.getFullYear(), now.getMonth(), 1);
      const thisMonthDonations = await Donation.find({
        createdAt: { $gte: firstOfMonth }
      });

      // Always show total collection from all donations (not filtered)
      const allDonations = await Donation.find({});
      const totalCollections = allDonations.reduce((sum, d) => sum + d.amount, 0);
      const filteredTotal = filteredDonations.reduce((sum, d) => sum + d.amount, 0);
      const thisMonthAmount = thisMonthDonations.reduce((sum, d) => sum + d.amount, 0);

      // Get unique donors by phone from filtered data
      const uniquePhones = new Set(filteredDonations.map(d => d.phone));
      const totalDonors = uniquePhones.size;

      const avgDonation = filteredDonations.length > 0 ? totalCollections / filteredDonations.length : 0;

      // Payment mode distribution based on filtered data
      const paymentModes = ['cash', 'card', 'upi', 'bankTransfer', 'cheque'];
      const paymentModeDistribution = paymentModes.map(mode => {
        const modeAmount = filteredDonations
          .filter(d => d.paymentMode === mode)
          .reduce((sum, d) => sum + d.amount, 0);

        const count = filteredDonations.filter(d => d.paymentMode === mode).length;
        return {
          mode,
          count,
          percentage: filteredTotal > 0 ? Math.round((modeAmount / filteredTotal) * 100) : 0,
          amount: modeAmount
        };
      });

      // Recent donations from filtered data (last 10)
      const recentDonations = filteredDonations
        .sort((a, b) => b.createdAt.getTime() - a.createdAt.getTime())
        .slice(0, 10)
        .map(d => ({
          name: d.name,
          amount: d.amount,
          paymentMode: d.paymentMode,
          createdAt: d.createdAt.toISOString()
        }));

      const stats = {
        totalCollection: totalCollections, // Always show total from all donations
        totalCollections: totalCollections, // Legacy support
        totalDonors,
        totalDonations: filteredDonations.length,
        thisMonth: thisMonthAmount,
        avgDonation: filteredDonations.length > 0 ? Math.round(filteredTotal / filteredDonations.length) : 0,
        paymentModeDistribution,
        recentDonations
      };

      // Cache for 30 seconds for faster loading
      dashboardCache.set(cacheKey, stats, 30);
      return stats;
    } catch (error) {
      console.error('Error fetching dashboard stats, falling back:', error);
      this.isMongoAvailable = false;
      return await this.inMemoryStorage.getDashboardStats(filters);
    }
  }

  async updateDonation(id: string, donation: any): Promise<IDonation | null> {
    if (!this.isMongoAvailable) {
      return await this.inMemoryStorage.updateDonation(id, donation);
    }

    try {
      return await Donation.findByIdAndUpdate(id, donation, { new: true });
    } catch (error) {
      console.error('Error updating donation, falling back:', error);
      this.isMongoAvailable = false;
      return await this.inMemoryStorage.updateDonation(id, donation);
    }
  }

  async deleteDonation(id: string): Promise<boolean> {
    if (!this.isMongoAvailable) {
      return await this.inMemoryStorage.deleteDonation(id);
    }

    try {
      const result = await Donation.findByIdAndDelete(id);
      return !!result;
    } catch (error) {
      console.error('Error deleting donation, falling back:', error);
      this.isMongoAvailable = false;
      return await this.inMemoryStorage.deleteDonation(id);
    }
  }

  async deleteAllDonations(): Promise<boolean> {
    if (!this.isMongoAvailable) {
      return await this.inMemoryStorage.deleteAllDonations();
    }

    try {
      await Donation.deleteMany({});
      // Also clear in-memory storage to stay in sync
      await this.inMemoryStorage.deleteAllDonations();
      return true;
    } catch (error) {
      console.error('Error deleting all donations, falling back:', error);
      this.isMongoAvailable = false;
      return await this.inMemoryStorage.deleteAllDonations();
    }
  }
}

// Export MongoDB storage instance
export const storage = new MongoDBStorage();

