import { z } from "zod";

// MongoDB Schema for donations
export const insertDonationSchema = z.object({
  receiptNo: z.string().min(1, "Receipt number is required"),
  name: z.string().min(1, "Name is required"),
  phone: z.string().regex(/^\d{10}$/, "Phone number must be exactly 10 digits"),
  community: z.enum(["any", "payiran", "chozhan", "pandiyan", "othaalan", "vizhiyan", "aadai", "aavan", "odhaalan", "semban"]),
  location: z.string().min(1, "Location is required"),
  address: z.string().optional(),
  amount: z.number().positive("Amount must be positive"),
  paymentMode: z.enum(["cash", "card", "upi", "bankTransfer", "cheque"]),
  inscription: z.boolean().default(false),
  donationDate: z.date().optional(),
});

// Types for MongoDB
export interface InsertDonation {
  receiptNo: string;
  name: string;
  phone: string;
  community: string;
  location: string;
  address?: string;
  amount: number;
  paymentMode: 'cash' | 'card' | 'upi' | 'bankTransfer' | 'cheque';
  inscription: boolean;
  donationDate?: Date;
}

export interface Donation extends InsertDonation {
  _id: string;
  createdAt: Date;
  updatedAt: Date;
}

export interface ReceiptSequence {
  _id: string;
  year: number;
  lastReceiptNumber: number;
  updatedAt: Date;
}

export interface DonorSummary {
  name: string;
  phone: string;
  location: string;
  community: string;
  totalAmount: number;
  donationCount: number;
  lastDonation: string;
  donations: Donation[];
}

export interface DashboardStats {
  totalCollection: number;
  totalCollections: number; // Legacy support
  totalDonors: number;
  totalDonations: number;
  thisMonth: number;
  avgDonation: number;
  paymentModeDistribution: {
    mode: string;
    count: number;
    percentage: number;
    amount: number;
  }[];
  recentDonations: {
    name: string;
    amount: number;
    paymentMode: string;
    createdAt: string;
  }[];
}

