              <p className="text-xs sm:text-sm text-gray-600 hidden lg:block truncate select-none">{t("templeSubtitle")}</p>
            </div>
          </div>

          {/* Navigation and Controls */}
          <div className="flex items-center space-x-1 sm:space-x-2 lg:space-x-4">
            {/* Logout Button for Authenticated Users */}
            {authStatus && authStatus.isAuthenticated && (
              <Button
                variant="ghost"
                size="sm"
                onClick={handleLogout}
                className="flex items-center space-x-1 px-2 py-1 text-xs sm:text-sm h-8 sm:h-9 text-red-600 hover:text-red-700"
              >
                <LogOut className="h-3 w-3 sm:h-4 sm:w-4" />
                <span className="font-medium hidden sm:inline">{language === 'en' ? 'Logout' : 'à®µà¯†à®³à®¿à®¯à¯‡à®±'}</span>
              </Button>
            )}

            {/* Language Toggle */}
            <Button
              variant="ghost"
              size="sm"
              onClick={toggleLanguage}
              className="flex items-center space-x-1 px-2 py-1 text-xs sm:text-sm h-8 sm:h-9"
            >
              <Languages className="h-3 w-3 sm:h-4 sm:w-4" />
              <span className="font-medium hidden sm:inline">{language === 'en' ? 'à®¤à®®à®¿à®´à¯' : 'English'}</span>
            </Button>

            {/* Desktop Navigation */}
            <nav className="hidden xl:flex space-x-1">
              {navItems.map((item) => {
                const IconComponent = {
                  Plus,
                  Search,
                  BarChart3,
                  Upload,
                  Link,
                  Settings
                }[item.icon] || Plus;

                return (
                  <Button
                    key={item.path}
                    variant="ghost"
                    size="sm"
