                  </Button>
                );
              })}
            </nav>

            {/* Tablet Navigation - Icons Only */}
            <nav className="hidden lg:flex xl:hidden space-x-1">
              {navItems.map((item) => {
                const IconComponent = {
                  Plus,
                  Search,
                  BarChart3,
                  Upload,
                  Link,
                  Settings
                }[item.icon] || Plus;

                return (
                  <Button
                    key={item.path}
                    variant="ghost"
                    size="sm"
                    className={`p-1 transition-colors h-8 w-8 ${
                      isActive(item.path)
                        ? "text-temple-primary bg-temple-accent"
                        : "text-gray-500 hover:text-gray-700"
                    }`}
