import { useState, useEffect, memo, useMemo } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { LoadingSpinner, FastSkeleton } from "@/components/ui/loading-spinner";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { formatCurrency, formatDate, formatPhoneNumber } from "@/lib/utils";
import { formatCurrencyTamil, formatDateTamil } from "@/lib/i18n";
import { useLanguage, useTranslation } from "@/contexts/LanguageContext";
import { Download, Plus, Edit, Trash2, LogOut, Settings, AlertTriangle, Calendar as CalendarIcon } from "lucide-react";
// Removed TypeScript imports - using JavaScript
import { apiRequest, queryClient } from "@/lib/queryClient";
import { Modal } from "@/components/ui/modal";
import AdminLogin from "./admin-login";
import AdminSettings from "./admin-settings";
import DonationForm from "./donation-form";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogDescription,
} from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";
import type { Donation } from "@/../../shared/mongodb-schema";

export default function AdminPanel() {
  const [isAuthenticated, setIsAuthenticated] = useState<boolean | null>(null);
  const [username, setUsername] = useState("");
  const [showSettings, setShowSettings] = useState(false);
  const [showManualEntry, setShowManualEntry] = useState(false);
  const [editingDonation, setEditingDonation] = useState<Donation | null>(null);
  const [deletingDonation, setDeletingDonation] = useState<Donation | null>(null);
  const [showDeleteAllDialog, setShowDeleteAllDialog] = useState(false);
  const { language } = useLanguage();
  const t = useTranslation();
  const { toast } = useToast();
  const [filters, setFilters] = useState({
    dateRange: "all",
    community: "any",
    paymentMode: "all",
    amountRange: "all",
    startDate: "",
    endDate: "",
  });

  // Check authentication status on component mount (optimized for speed)
  useEffect(() => {
    const checkAuth = async () => {
      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 1500); // Fast 1.5 second timeout

      try {
        const response = await fetch("/api/auth/status", {
          credentials: "include",
          signal: controller.signal
        });
        clearTimeout(timeoutId);
        const data = await response.json();
        setIsAuthenticated(data.isAuthenticated);
        setUsername(data.username || "");
      } catch (error) {
        clearTimeout(timeoutId);
        setIsAuthenticated(false);
      }
    };
    checkAuth();
  }, []);

  const logoutMutation = useMutation({
    mutationFn: async () => {
      const response = await fetch("/api/auth/logout", {
        method: "POST",
        credentials: "include",
      });
      return response.json();
    },
    onSuccess: () => {
      setIsAuthenticated(false);
      setUsername("");
    },
  });

  const handleLoginSuccess = () => {
    setIsAuthenticated(true);
    // Re-check authentication status to get username (fast version)
    const checkAuth = async () => {
      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 1000); // Very fast 1 second timeout

      try {
        const response = await fetch("/api/auth/status", {
          credentials: "include",
          signal: controller.signal
        });
        clearTimeout(timeoutId);
        const data = await response.json();
        setIsAuthenticated(data.isAuthenticated);
        setUsername(data.username || "");
      } catch (error) {
        clearTimeout(timeoutId);
        setIsAuthenticated(false);
      }
    };
    checkAuth();
  };

  const { data: donations = [], isLoading } = useQuery<Donation[]>({
    queryKey: ["/api/donations", filters],
    queryFn: async () => {
      const params = new URLSearchParams();
      Object.entries(filters).forEach(([key, value]) => {
        if (value && value !== "all" && value !== "any") {
          params.append(key, value);
        }
      });

      const response = await fetch(`/api/donations?${params}`, {
        credentials: "include",
      });

      if (!response.ok) {
        throw new Error("Failed to fetch donations");
      }

      return response.json();
    },
    enabled: isAuthenticated === true, // Only run query when authenticated
  });

  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      const response = await fetch(`/api/donations/${id}`, {
        method: "DELETE",
        credentials: "include",
      });
      if (!response.ok) {
        throw new Error("Failed to delete donation");
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/donations"] });
      toast({
        title: "Success",
        description: "Donation deleted successfully",
      });
      setDeletingDonation(null);
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to delete donation",
        variant: "destructive",
      });
    },
  });

  const deleteAllMutation = useMutation({
    mutationFn: async () => {
      const response = await fetch("/api/donations/all", {
        method: "DELETE",
        credentials: "include",
      });
      if (!response.ok) {
        throw new Error("Failed to delete all donations");
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/donations"] });
      queryClient.invalidateQueries({ queryKey: ["/api/dashboard/stats"] });
      toast({
        title: "Success",
        description: language === "ta" ? "à®…à®©à¯ˆà®¤à¯à®¤à¯ à®¨à®©à¯à®•à¯Šà®Ÿà¯ˆà®•à®³à¯à®®à¯ à®µà¯†à®±à¯à®±à®¿à®•à®°à®®à®¾à®• à®¨à¯€à®•à¯à®•à®ªà¯à®ªà®Ÿà¯à®Ÿà®©" : "All donations deleted successfully",
      });
      setShowDeleteAllDialog(false);
    },
    onError: () => {
      toast({
        title: "Error",
        description: language === "ta" ? "à®…à®©à¯ˆà®¤à¯à®¤à¯ à®¨à®©à¯à®•à¯Šà®Ÿà¯ˆà®•à®³à¯ˆà®¯à¯à®®à¯ à®¨à¯€à®•à¯à®• à®®à¯à®Ÿà®¿à®¯à®µà®¿à®²à¯à®²à¯ˆ" : "Failed to delete all donations",
        variant: "destructive",
      });
    },
  });

  const handleLogout = () => {
    logoutMutation.mutate();
  };

  const handleDelete = (donation: Donation) => {
    setDeletingDonation(donation);
  };

  const confirmDelete = () => {
    if (deletingDonation) {
      deleteMutation.mutate((deletingDonation as any)._id || (deletingDonation as any).id);
    }
  };

  const confirmDeleteAll = () => {
    deleteAllMutation.mutate();
  };

  const handleExport = async () => {
    try {
      const response = await fetch("/api/donations/export", {
        credentials: "include",
      });

      if (response.ok) {
        const blob = await response.blob();
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement("a");
        a.href = url;
        a.download = "donations.csv";
        a.click();
        window.URL.revokeObjectURL(url);
      }
    } catch (error) {
      console.error("Export failed:", error);
    }
  };

  const getPaymentModeColor = (mode: string) => {
    const colors: Record<string, string> = {
      cash: "bg-green-100 text-green-800",
      upi: "bg-blue-100 text-blue-800",
      card: "bg-purple-100 text-purple-800",
      "bank-transfer": "bg-orange-100 text-orange-800",
      cheque: "bg-gray-100 text-gray-800",
    };
    return colors[mode] || "bg-gray-100 text-gray-800";
  };

  // Show login form if not authenticated
  if (isAuthenticated === false) {
    return <AdminLogin onLoginSuccess={handleLoginSuccess} />;
  }

  // Show loading while checking authentication
  if (isAuthenticated === null) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-lg">
          {language === "en" ? "Loading..." : "à®à®±à¯à®±à¯à®•à®¿à®±à®¤à¯..."}
        </div>
      </div>
    );
  }

  // Show settings page if requested
  if (showSettings) {
    return <AdminSettings onBack={() => setShowSettings(false)} />;
  }

  if (isLoading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-lg">
          {language === "en"
            ? "Loading donations..."
            : "à®¨à®©à¯à®•à¯Šà®Ÿà¯ˆà®•à®³à¯ˆ à®à®±à¯à®±à¯à®•à®¿à®±à®¤à¯..."}
        </div>
      </div>
    );
  }

  return (
    <div className="w-full space-y-4 sm:space-y-6 px-2 sm:px-4">
      <div className="flex flex-col sm:flex-row sm:justify-between sm:items-center gap-3">
        <div className="flex items-center gap-3">
          <h2 className="text-lg sm:text-2xl font-semibold text-gray-900">
            {t("adminPanel")}
          </h2>
          <div className="text-sm text-gray-500">
            {language === "en"
              ? `Welcome, ${username}`
              : `à®µà®°à®µà¯‡à®±à¯à®•à®¿à®±à¯‹à®®à¯, ${username}`}
          </div>
        </div>
        <div className="flex flex-col sm:flex-row gap-2">
          <Button
            className="bg-temple-primary hover:bg-temple-primary/90 text-sm sm:text-base h-10 sm:h-11 disabled:opacity-50 disabled:cursor-not-allowed"
            onClick={(e) => {
              e.preventDefault();
              setShowManualEntry(true);
            }}
          >
            <Plus className="h-4 w-4 mr-2" />
            {language === "en" ? "Add Manual Entry" : "à®•à¯ˆà®®à¯à®±à¯ˆ à®ªà®¤à®¿à®µà¯ à®šà¯‡à®°à¯"}
          </Button>
          <Button
            variant="outline"
            onClick={(e) => {
              e.preventDefault();
              handleExport();
            }}
            className="text-sm sm:text-base h-10 sm:h-11 disabled:opacity-50 disabled:cursor-not-allowed"
          >
            <Download className="h-4 w-4 mr-2" />
            {t("export")}
          </Button>
          <Button
            variant="outline"
            onClick={(e) => {
              e.preventDefault();
              setShowSettings(true);
            }}
            className="text-sm sm:text-base h-10 sm:h-11 disabled:opacity-50 disabled:cursor-not-allowed"
          >
            <Settings className="h-4 w-4 mr-2" />
            {language === "en" ? "Settings" : "à®…à®®à¯ˆà®ªà¯à®ªà¯à®•à®³à¯"}
          </Button>
          <Button
            variant="outline"
            onClick={(e) => {
              e.preventDefault();
              if (donations.length > 0 && !deleteAllMutation.isPending) {
                setShowDeleteAllDialog(true);
              }
            }}
            className="text-sm sm:text-base h-10 sm:h-11 text-red-600 hover:text-red-700 border-red-200 hover:border-red-300"
            disabled={donations.length === 0 || deleteAllMutation.isPending}
          >
            <Trash2 className="h-4 w-4 mr-2" />
            {deleteAllMutation.isPending 
              ? (language === "en" ? "Deleting..." : "à®¨à¯€à®•à¯à®•à¯à®•à®¿à®±à®¤à¯...")
              : (language === "en" ? "Delete All" : "à®…à®©à¯ˆà®¤à¯à®¤à¯ˆà®¯à¯à®®à¯ à®¨à¯€à®•à¯à®•à¯")
            }
          </Button>
          <Button
            variant="outline"
            onClick={(e) => {
              e.preventDefault();
              if (!logoutMutation.isPending) {
                handleLogout();
              }
            }}
            className="text-sm sm:text-base h-10 sm:h-11 text-red-600 hover:text-red-700 disabled:opacity-50 disabled:cursor-not-allowed"
            disabled={logoutMutation.isPending}
          >
            <LogOut className="h-4 w-4 mr-2" />
            {logoutMutation.isPending
              ? (language === "en" ? "Logging out..." : "à®µà¯†à®³à®¿à®¯à¯‡à®±à¯à®•à®¿à®±à®¤à¯...")
              : (language === "en" ? "Logout" : "à®µà¯†à®³à®¿à®¯à¯‡à®±à¯")
            }
          </Button>
        </div>
      </div>

      {/* Filters */}
      <Card className="shadow-lg">
        <CardContent className="p-3 sm:p-6">
          <h3 className="text-base sm:text-lg font-semibold text-gray-900 mb-3 sm:mb-4">
            {language === "en" ? "Filters" : "à®µà®Ÿà®¿à®•à®Ÿà¯à®Ÿà®¿à®•à®³à¯"}
          </h3>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-3 sm:gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Date Range
              </label>
              <Select
                value={filters.dateRange}
                onValueChange={(value) => {
                  setFilters((prev) => ({ ...prev, dateRange: value }));
                  if (value !== "custom") {
                    setFilters((prev) => ({ ...prev, startDate: "", endDate: "" }));
                  }
                }}
              >
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Time</SelectItem>
                  <SelectItem value="today">Today</SelectItem>
                  <SelectItem value="week">This Week</SelectItem>
                  <SelectItem value="month">This Month</SelectItem>
