import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Key, User, AlertCircle, CheckCircle, Eye, EyeOff } from "lucide-react";
import { useLanguage, useTranslation } from "@/contexts/LanguageContext";
import { apiRequest } from "@/lib/queryClient";

const credentialsSchema = z.object({
  currentPassword: z.string().min(1, "Current password is required"),
  newUsername: z.string().min(3, "Username must be at least 3 characters"),
  newPassword: z.string().min(6, "Password must be at least 6 characters"),
  confirmPassword: z.string().min(1, "Please confirm your password"),
}).refine((data) => data.newPassword === data.confirmPassword, {
  message: "Passwords don't match",
  path: ["confirmPassword"],
});

type CredentialsForm = z.infer<typeof credentialsSchema>;

interface AdminSettingsProps {
  onBack: () => void;
}

export default function AdminSettings({ onBack }: AdminSettingsProps) {
  const { language } = useLanguage();
  const t = useTranslation();
  const queryClient = useQueryClient();
  const [message, setMessage] = useState<{ type: "success" | "error"; text: string } | null>(null);
  const [showCurrentPassword, setShowCurrentPassword] = useState(false);
  const [showNewPassword, setShowNewPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);

  const form = useForm<CredentialsForm>({
    resolver: zodResolver(credentialsSchema),
    defaultValues: {
      currentPassword: "",
      newUsername: "",
      newPassword: "",
      confirmPassword: "",
    },
  });

  const changeCredentialsMutation = useMutation({
    mutationFn: async (data: CredentialsForm) => {
      console.log('Attempting to change credentials...', { newUsername: data.newUsername });
      const response = await apiRequest("POST", "/api/auth/change-credentials", data);
      return await response.json();
    },
    onSuccess: (data) => {
      console.log('Credentials changed successfully:', data);
      const successMessage = language === "en" 
        ? "Credentials updated successfully! Redirecting to dashboard..." 
        : "à®…à®±à®¿à®®à¯à®• à®¤à®•à®µà®²à¯ à®µà¯†à®±à¯à®±à®¿à®•à®°à®®à®¾à®• à®®à®¾à®±à¯à®±à®ªà¯à®ªà®Ÿà¯à®Ÿà®¤à¯! à®Ÿà®¾à®·à¯à®ªà¯‹à®°à¯à®Ÿà¯à®•à¯à®•à¯ à®…à®©à¯à®ªà¯à®ªà®ªà¯à®ªà®Ÿà¯à®•à®¿à®±à®¤à¯...";
      setMessage({ type: "success", text: successMessage });
      form.reset();

      // Invalidate auth status to refresh session
      queryClient.invalidateQueries({ queryKey: ["/api/auth/status"] });

      // Redirect to dashboard after successful credential change
      setTimeout(() => {
        onBack();
      }, 2000); // Wait 2 seconds to show success message
    },
    onError: (error: any) => {
      console.error('Failed to change credentials:', error);
      const errorMessage = error.message || (language === "en" ? "Failed to change credentials" : "à®…à®±à®¿à®®à¯à®• à®¤à®•à®µà®²à¯ à®®à®¾à®±à¯à®±à¯à®µà®¤à®¿à®²à¯ à®¤à¯‹à®²à¯à®µà®¿");
      setMessage({ type: "error", text: errorMessage });
    },
  });

  const onSubmit = (data: CredentialsForm) => {
    setMessage(null);
    changeCredentialsMutation.mutate(data);
  };

  return (
    <div className="w-full max-w-2xl mx-auto space-y-6 px-4">
      <div className="flex items-center gap-4 mb-6">
        <Button variant="outline" onClick={onBack}>
          {language === "en" ? "â† Back to Admin Panel" : "â† à®¨à®¿à®°à¯à®µà®¾à®• à®ªà®¾à®Ÿà¯à®Ÿà¯à®•à¯à®•à¯ à®¤à®¿à®°à¯à®®à¯à®ªà¯"}
        </Button>
        <h1 className="text-2xl font-bold text-gray-900">
          {language === "en" ? "Admin Settings" : "à®¨à®¿à®°à¯à®µà®¾à®• à®…à®®à¯ˆà®ªà¯à®ªà¯à®•à®³à¯"}
        </h1>
      </div>

      <Card className="shadow-lg">
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Key className="h-5 w-5 text-temple-primary" />
            {language === "en" ? "Change Admin Credentials" : "à®¨à®¿à®°à¯à®µà®¾à®• à®…à®±à®¿à®®à¯à®• à®¤à®•à®µà®²à¯ˆ à®®à®¾à®±à¯à®±à¯"}
          </CardTitle>
        </CardHeader>
        <CardContent>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
            {message && (
              <Alert variant={message.type === "error" ? "destructive" : "default"}>
                <div className="flex items-center gap-2">
                  {message.type === "error" ? (
                    <AlertCircle className="h-4 w-4" />
                  ) : (
                    <CheckCircle className="h-4 w-4 text-green-600" />
                  )}
                  <AlertDescription>{message.text}</AlertDescription>
                </div>
              </Alert>
            )}

            <div className="space-y-4">
              <div>
                <Label htmlFor="currentPassword" className="text-sm font-medium">
                  {language === "en" ? "Current Password" : "à®¤à®±à¯à®ªà¯‹à®¤à¯ˆà®¯ à®•à®Ÿà®µà¯à®šà¯à®šà¯Šà®²à¯"}
                </Label>
                <div className="relative mt-1">
                  <Key className="absolute left-3 top-3 h-4 w-4 text-gray-400" />
                  <Input
                    id="currentPassword"
                    type={showCurrentPassword ? "text" : "password"}
                    placeholder={language === "en" ? "Enter current password" : "à®¤à®±à¯à®ªà¯‹à®¤à¯ˆà®¯ à®•à®Ÿà®µà¯à®šà¯à®šà¯Šà®²à¯à®²à¯ˆ à®‰à®³à¯à®³à®¿à®Ÿà®µà¯à®®à¯"}
                    className="pl-10 pr-10 h-11"
                    {...form.register("currentPassword")}
                  />
                  <button
                    type="button"
                    className="absolute right-3 top-3 h-4 w-4 text-gray-400 hover:text-gray-600"
                    onClick={() => setShowCurrentPassword(!showCurrentPassword)}
                    title={showCurrentPassword ? 
                      (language === "en" ? "Hide password" : "à®•à®Ÿà®µà¯à®šà¯à®šà¯Šà®²à¯à®²à¯ˆ à®®à®±à¯ˆà®•à¯à®•") : 
                      (language === "en" ? "Show password" : "à®•à®Ÿà®µà¯à®šà¯à®šà¯Šà®²à¯à®²à¯ˆà®•à¯ à®•à®¾à®Ÿà¯à®Ÿ")
                    }
                  >
                    {showCurrentPassword ? <Eye className="h-4 w-4" /> : <EyeOff className="h-4 w-4" />}
                  </button>
                </div>
                {form.formState.errors.currentPassword && (
                  <p className="text-sm text-red-600 mt-1">
                    {form.formState.errors.currentPassword.message}
                  </p>
                )}
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="newUsername" className="text-sm font-medium">
                    {language === "en" ? "New Username" : "à®ªà¯à®¤à®¿à®¯ à®ªà®¯à®©à®°à¯ à®ªà¯†à®¯à®°à¯"}
                  </Label>
                  <div className="relative mt-1">
                    <User className="absolute left-3 top-3 h-4 w-4 text-gray-400" />
                    <Input
                      id="newUsername"
                      type="text"
                      placeholder={language === "en" ? "Enter new username" : "à®ªà¯à®¤à®¿à®¯ à®ªà®¯à®©à®°à¯ à®ªà¯†à®¯à®°à¯ˆ à®‰à®³à¯à®³à®¿à®Ÿà®µà¯à®®à¯"}
                      className="pl-10 h-11"
                      {...form.register("newUsername")}
                    />
                  </div>
                  {form.formState.errors.newUsername && (
                    <p className="text-sm text-red-600 mt-1">
                      {form.formState.errors.newUsername.message}
                    </p>
                  )}
                </div>

                <div>
                  <Label htmlFor="newPassword" className="text-sm font-medium">
                    {language === "en" ? "New Password" : "à®ªà¯à®¤à®¿à®¯ à®•à®Ÿà®µà¯à®šà¯à®šà¯Šà®²à¯"}
                  </Label>
                  <div className="relative mt-1">
                    <Key className="absolute left-3 top-3 h-4 w-4 text-gray-400" />
                    <Input
                      id="newPassword"
                      type={showNewPassword ? "text" : "password"}
                      placeholder={language === "en" ? "Enter new password" : "à®ªà¯à®¤à®¿à®¯ à®•à®Ÿà®µà¯à®šà¯à®šà¯Šà®²à¯à®²à¯ˆ à®‰à®³à¯à®³à®¿à®Ÿà®µà¯à®®à¯"}
                      className="pl-10 pr-10 h-11"
                      {...form.register("newPassword")}
                    />
                    <button
                      type="button"
                      className="absolute right-3 top-3 h-4 w-4 text-gray-400 hover:text-gray-600"
                      onClick={() => setShowNewPassword(!showNewPassword)}
                      title={showNewPassword ? 
                        (language === "en" ? "Hide password" : "à®•à®Ÿà®µà¯à®šà¯à®šà¯Šà®²à¯à®²à¯ˆ à®®à®±à¯ˆà®•à¯à®•") : 
                        (language === "en" ? "Show password" : "à®•à®Ÿà®µà¯à®šà¯à®šà¯Šà®²à¯à®²à¯ˆà®•à¯ à®•à®¾à®Ÿà¯à®Ÿ")
                      }
                    >
                      {showNewPassword ? <Eye className="h-4 w-4" /> : <EyeOff className="h-4 w-4" />}
                    </button>
                  </div>
                  {form.formState.errors.newPassword && (
                    <p className="text-sm text-red-600 mt-1">
                      {form.formState.errors.newPassword.message}
                    </p>
                  )}
                </div>
              </div>

              <div>
                <Label htmlFor="confirmPassword" className="text-sm font-medium">
                  {language === "en" ? "Confirm New Password" : "à®ªà¯à®¤à®¿à®¯ à®•à®Ÿà®µà¯à®šà¯à®šà¯Šà®²à¯à®²à¯ˆ à®‰à®±à¯à®¤à®¿à®ªà¯à®ªà®Ÿà¯à®¤à¯à®¤à¯"}
                </Label>
                <div className="relative mt-1">
                  <Key className="absolute left-3 top-3 h-4 w-4 text-gray-400" />
                  <Input
                    id="confirmPassword"
                    type={showConfirmPassword ? "text" : "password"}
                    placeholder={language === "en" ? "Confirm new password" : "à®ªà¯à®¤à®¿à®¯ à®•à®Ÿà®µà¯à®šà¯à®šà¯Šà®²à¯à®²à¯ˆ à®‰à®±à¯à®¤à®¿à®ªà¯à®ªà®Ÿà¯à®¤à¯à®¤à®µà¯à®®à¯"}
                    className="pl-10 pr-10 h-11"
                    {...form.register("confirmPassword")}
                  />
                  <button
                    type="button"
                    className="absolute right-3 top-3 h-4 w-4 text-gray-400 hover:text-gray-600"
                    onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                    title={showConfirmPassword ? 
