import { memo, useState } from "react";
import { useLanguage } from "@/contexts/LanguageContext";
import { useQuery } from "@tanstack/react-query";
import { format } from "date-fns";
import { cn } from "@/lib/utils";

import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Popover, PopoverContent, PopoverTrigger } from "@/components/ui/popover";
import { Calendar as CalendarComponent } from "@/components/ui/calendar";
import { StatsCard } from "@/components/ui/stats-card";
import { DashboardSkeleton } from "@/components/ui/fast-loading";
import { 
  Calendar, 
  CalendarIcon, 
  Download, 
  Coins, 
  Users, 
  CreditCard, 
  TrendingUp 
} from "lucide-react";

interface DashboardStats {
  totalCollections: number;
  totalDonors: number;
  totalDonations: number;
  avgDonation: number;
  paymentModeDistribution: Array<{ mode: string; count: number; percentage: number }>;
  recentDonations: Array<{ name: string; amount: number; paymentMode: string; createdAt: string }>;
}

const Dashboard = memo(function Dashboard() {
  const { language } = useLanguage();
  const [dateRange, setDateRange] = useState<string>("all");
  const [customStartDate, setCustomStartDate] = useState<Date | undefined>(undefined);
  const [customEndDate, setCustomEndDate] = useState<Date | undefined>(undefined);

  // Generate query parameters based on date range selection  
  const getDashboardQuery = () => {
    const now = new Date();

    if (dateRange === "custom") {
      if (customStartDate && customEndDate) {
        return `/api/dashboard/stats?dateRange=custom&startDate=${format(customStartDate, "yyyy-MM-dd")}&endDate=${format(customEndDate, "yyyy-MM-dd")}`;
      }
      return `/api/dashboard/stats?dateRange=all`;
    }

    return `/api/dashboard/stats?dateRange=${dateRange}`;
  };

  const { data: stats, isLoading, isError } = useQuery<DashboardStats>({
    queryKey: ['dashboard-stats', dateRange, customStartDate, customEndDate],
    queryFn: async () => {
      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 10000); // 10-second timeout

      try {
        const response = await fetch(getDashboardQuery(), {
          credentials: 'include',
          signal: controller.signal,
          headers: {
            'Cache-Control': 'max-age=120', // Request caching
          }
        });
        clearTimeout(timeoutId);

        if (!response.ok) throw new Error(`HTTP ${response.status}: Failed to fetch dashboard stats`);
        return response.json();
      } catch (error) {
        clearTimeout(timeoutId);
        throw error;
      }
    },
    staleTime: 2 * 60 * 1000, // 2 minutes stale time
    gcTime: 10 * 60 * 1000, // 10 minutes garbage collection
    refetchOnWindowFocus: false,
    refetchOnMount: false,
    retry: 2,
    retryDelay: 1000,
  });

  const useTranslation = () => {
    return {
      totalCollection: language === "ta" ? "à®®à¯Šà®¤à¯à®¤ à®šà¯‡à®•à®°à®¿à®ªà¯à®ªà¯" : "Total Collection",
      totalDonors: language === "ta" ? "à®®à¯Šà®¤à¯à®¤ à®¨à®©à¯à®•à¯Šà®Ÿà¯ˆà®¯à®¾à®³à®°à¯à®•à®³à¯" : "Total Donors",
      totalDonations: language === "ta" ? "à®®à¯Šà®¤à¯à®¤ à®¨à®©à¯à®•à¯Šà®Ÿà¯ˆà®•à®³à¯" : "Total Donations",
      avgDonation: language === "ta" ? "à®šà®°à®¾à®šà®°à®¿ à®¨à®©à¯à®•à¯Šà®Ÿà¯ˆ" : "Average Donation",
      paymentModeDistribution: language === "ta" ? "à®ªà®£à®®à¯ à®šà¯†à®²à¯à®¤à¯à®¤à¯à®®à¯ à®®à¯à®±à¯ˆ à®µà®¿à®¨à®¿à®¯à¯‹à®•à®®à¯" : "Payment Mode Distribution",
      recentDonations: language === "ta" ? "à®šà®®à¯€à®ªà®¤à¯à®¤à®¿à®¯ à®¨à®©à¯à®•à¯Šà®Ÿà¯ˆà®•à®³à¯" : "Recent Donations",
    };
  };

  const t = useTranslation();

  // Currency formatting functions
  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(amount);
  };

  const formatCurrencyTamil = (amount: number) => {
    const formatted = new Intl.NumberFormat('en-IN', {
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(amount);
    return `â‚¹${formatted}`;
  };

  // Export functionality
  const handleExport = async () => {
    try {
      const queryUrl = getDashboardQuery().replace('/stats', '/export');
      const response = await fetch(queryUrl, {
        method: 'GET',
        credentials: 'include',
      });

      if (response.ok) {
        const blob = await response.blob();
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.style.display = 'none';
        a.href = url;
        a.download = `donations_${dateRange}_${new Date().toISOString().split('T')[0]}.csv`;
        document.body.appendChild(a);
        a.click();
        window.URL.revokeObjectURL(url);
      }
    } catch (error) {
      console.error('Export failed:', error);
    }
  };

  if (isLoading || !stats) {
    return <DashboardSkeleton />;
  }

  if (isError) {
    return (
      <div className="p-6 text-center">
