import { useState, useEffect } from "react";
import { useForm } from "react-hook-form";
import { useMutation, useQuery } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { formatCurrency } from "@/lib/utils";
// Removed TypeScript imports - using JavaScript
import { useTranslation, useLanguage } from "@/contexts/LanguageContext";
import { formatCurrencyTamil } from "@/lib/i18n";
import { Plus, CheckCircle } from "lucide-react";
import type { Donation, InsertDonation } from "@/../../shared/schema";

export default function DonationForm({
  initialData,
  onSuccess,
}: {
  initialData?: Donation;
  onSuccess: () => void;
}) {
  const { toast } = useToast();
  const { language } = useLanguage();
  const t = useTranslation();
  const [donorHistory, setDonorHistory] = useState<any>(null);
  const [showSuccessDialog, setShowSuccessDialog] = useState<boolean>(false);
  const [successData, setSuccessData] = useState<any>(null);
  const [receiptError, setReceiptError] = useState<string>("");

  const isEditMode = !!initialData;

  const form = useForm({
    defaultValues: initialData
      ? {
          receiptNo: initialData.receiptNo,
          name: initialData.name,
          phone: initialData.phone,
          community: (initialData.community as any) || undefined,
          location: initialData.location || "",
          address: initialData.address || "",
          amount: initialData.amount,
          paymentMode: initialData.paymentMode as any,
          inscription: initialData.inscription,
          donationDate: initialData.donationDate
            ? new Date(initialData.donationDate).toISOString().split("T")[0]
            : undefined,
        }
      : {
          receiptNo: "",
          name: "",
          phone: "",
          community: undefined,
          location: "",
          address: "",
          amount: undefined,
          paymentMode: "cash",
          inscription: false,
          donationDate: "",
        },
  });

  const createDonationMutation = useMutation({
    mutationFn: async (data: any) => {
      if (isEditMode && initialData) {
        // Update existing donation
        const donationId = (initialData as any)._id || (initialData as any).id;
        const response = await fetch(`/api/donations/${donationId}`, {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
          },
          credentials: "include",
          body: JSON.stringify(data),
        });
        if (!response.ok) {
          throw new Error("Failed to update donation");
        }
        return response.json();
      } else {
        // Create new donation
        const response = await fetch("/api/donations", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          credentials: "include",
          body: JSON.stringify(data),
        });

        if (!response.ok) {
          const errorData = await response.json().catch(() => ({}));
          if (response.status === 401) {
            throw new Error("Authentication required. Please login to admin panel first.");
          }
          throw new Error(errorData.message || "Failed to create donation");
        }

        return response.json();
      }
    },
    onSuccess: (result) => {
      if (isEditMode) {
        // For edit mode, call onSuccess callback if provided
        if (onSuccess) {
          onSuccess();
        }
        toast({
          title: "Success",
          description: "Donation updated successfully",
        });
      } else {
        // For create mode, show success dialog and toast
        setSuccessData({
          receiptNo: result.receiptNo,
          amount: result.amount,
          name: result.name,
          community: result.community,
          paymentMode: result.paymentMode,
        });
        setShowSuccessDialog(true);

        // Show success toast immediately
        toast({
          title:
            language === "en"
              ? "âœ… Donation Added Successfully!"
              : "âœ… à®¨à®©à¯à®•à¯Šà®Ÿà¯ˆ à®µà¯†à®±à¯à®±à®¿à®•à®°à®®à®¾à®• à®šà¯‡à®°à¯à®•à¯à®•à®ªà¯à®ªà®Ÿà¯à®Ÿà®¤à¯!",
          description:
            language === "en"
              ? `Receipt No: ${result.receiptNo} | Amount: â‚¹${result.amount}`
              : `à®°à®šà¯€à®¤à¯ à®à®£à¯: ${result.receiptNo} | à®¤à¯Šà®•à¯ˆ: â‚¹${result.amount}`,
          duration: 5000,
        });

        // Reset form and state
        form.reset({
          receiptNo: "",
          name: "",
          phone: "",
          community: undefined,
          location: "",
          amount: undefined,
          paymentMode: "cash",
          inscription: false,
          donationDate: undefined,
        });
        setDonorHistory(null);

        // Invalidate queries to refresh data across all components
        queryClient.invalidateQueries({ queryKey: ["/api/dashboard/stats"] });
        queryClient.invalidateQueries({ queryKey: ["/api/donations"] });
        queryClient.invalidateQueries({ queryKey: ["/api/donors"] });
      }
    },
    onError: (error: any) => {
      console.error("Donation error:", error);

      // Handle authentication error
      if (error.message?.includes("Authentication required")) {
        toast({
          title: language === "en" ? "âŒ Authentication Required" : "âŒ à®…à®™à¯à®•à¯€à®•à®¾à®°à®®à¯ à®¤à¯‡à®µà¯ˆ",
          description: language === "en" 
            ? "Please login to admin panel first to add donations." 
            : "à®¨à®©à¯à®•à¯Šà®Ÿà¯ˆ à®šà¯‡à®°à¯à®•à¯à®• à®®à¯à®¤à®²à®¿à®²à¯ à®¨à®¿à®°à¥à¤µà¤¾à®• à®ªà®¾à®Ÿà¥à®Ÿà®¿à®²à¯ à®‰à®³à¯à®¨à¯à®´à¥ˆà®•à¤µà¥à¤®à¯.",
          variant: "destructive",
          duration: 8000,
        });
        return;
      }

      // Handle specific receipt number duplicate error
      if (error.message?.includes("Receipt number already exists") || error.code === "DUPLICATE_RECEIPT") {
        setReceiptError(language === "en" ? "Receipt number already exists" : "à®°à®šà¯€à®¤à¯ à®à®£à¯ à®à®±à¯à®•à®©à®µà¯‡ à®‰à®³à¯à®³à®¤à¯");
        form.setError("receiptNo", {
          type: "manual",
          message: language === "en" ? "Receipt number already exists" : "à®°à®šà¯€à®¤à¯ à®à®£à¯ à®à®±à¯à®•à®©à®µà¯‡ à®‰à®³à¯à®³à®¤à¯"
        });
        toast({
          title: language === "en" ? "âŒ Error" : "âŒ à®ªà®¿à®´à¯ˆ",
          description: language === "en" ? "Receipt number already exists. Please use a different receipt number." : "à®°à®šà¯€à®¤à¯ à®à®£à¯ à®à®±à¯à®•à®©à®µà¯‡ à®‰à®³à¯à®³à®¤à¯. à®µà¯‡à®±à¯ à®°à®šà¯€à®¤à¯ à®à®£à¯à®£à¯ˆà®ªà¯ à®ªà®¯à®©à¯à®ªà®Ÿà¯à®¤à¯à®¤à®µà¯à®®à¯.",
          variant: "destructive",
          duration: 5000,
        });
      } else {
        toast({
          title: language === "en" ? "âŒ Error" : "âŒ à®ªà®¿à®´à¯ˆ",
          description: error.message || t("donationError"),
          variant: "destructive",
          duration: 5000,
        });
      }
    },
  });

  const checkReceiptNumber = async (receiptNo: string) => {
    if (receiptNo && receiptNo.trim() !== "" && !isEditMode) {
      try {
        const response = await fetch(`/api/donations/check-receipt/${encodeURIComponent(receiptNo)}`, {
          credentials: "include",
        });

        if (response.status === 401) {
          // Authentication required - show warning but don't block form
          toast({
            title: language === "en" ? "âš ï¸ Authentication Required" : "âš ï¸ à®…à®™à¯à®•à¥€à®•à®¾à®°à®®à¯ à®¤à¯‡à®µà¯ˆ",
            description: language === "en" 
              ? "Please login to admin panel to enable receipt validation." 
              : "à®°à®šà¯€à®¤à¯ à®šà®°à®¿à®ªà®¾à®°à¥à¤ªà¤£à¯ˆ à¤‡à¤·à¯à¤Ÿà¤®à¤¾à¤šà¥à¤›à¥à¤®à¥ à®¨à®¿à¤°à¥à¤µà¤¾à¤• à®ªà¦¾à¤Ÿà¥à¦Ÿà®¿à®²à¯ à¤‰à®³à¥à®¨à¥à®´à¥ˆà¦—à¤¾à¤µà§à¦®à¥.",
            variant: "destructive",
            duration: 5000,
          });
          return;
        }

        if (response.ok) {
          const result = await response.json();
          if (result.exists) {
            const errorMsg = language === "en" ? "Receipt number already exists" : "à®°à®šà¯€à®¤à¯ à®à®£à¯ à®à®±à¯à®•à®©à®µà¯‡ à®‰à®³à¯à®³à®¤à¯";
            setReceiptError(errorMsg);
            form.setError("receiptNo", {
              type: "manual",
              message: errorMsg
            });
          } else {
            setReceiptError("");
            form.clearErrors("receiptNo");
          }
        }
      } catch (error) {
        console.error("Error checking receipt number:", error);
      }
    } else {
      setReceiptError("");
      form.clearErrors("receiptNo");
    }
  };

  const checkExistingDonor = async (phone: string) => {
    if (phone.length >= 10) {
      try {
        const response = await fetch(`/api/donors/${phone}`, {
          credentials: "include",
        });

        if (response.status === 401) {
          // Authentication required - continue without pre-filling
          setDonorHistory(null);
          return;
        }

        if (response.ok) {
          const donor = await response.json();
          setDonorHistory(donor);
          // Pre-fill form with existing donor data
          form.setValue("name", donor.name);
          form.setValue("location", donor.location || "");
          form.setValue("community", donor.community || "");
        } else {
          setDonorHistory(null);
        }
      } catch (error) {
        setDonorHistory(null);
      }
    } else {
      setDonorHistory(null);
    }
  };

  const onSubmit = (data: any) => {
    // Check if there's a receipt error before submitting
    if (receiptError) {
      toast({
        title: language === "en" ? "âŒ Error" : "âŒ à®ªà®¿à®´à¯ˆ",
