import { useState } from "react";
import { useTranslation } from "@/contexts/LanguageContext";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Separator } from "@/components/ui/separator";
import { useToast } from "@/hooks/use-toast";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { Upload, FileText, Download, AlertCircle, CheckCircle } from "lucide-react";
import * as XLSX from 'xlsx';

interface ImportResult {
  success: boolean;
  totalRecords: number;
  successCount: number;
  failureCount: number;
  errors: string[];
}

export default function ImportData() {
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [importResult, setImportResult] = useState<ImportResult | null>(null);
  const t = useTranslation();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const importMutation = useMutation({
    mutationFn: async (file: File) => {
      const formData = new FormData();
      formData.append('file', file);

      const response = await fetch('/api/donations/import', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      return response.json();
    },
    onSuccess: (result: ImportResult) => {
      setImportResult(result);

      if (result.success) {
        toast({
          title: t("success"),
          description: `Successfully imported ${result.successCount} of ${result.totalRecords} donations`,
        });
        // Invalidate donations cache to refresh data
        queryClient.invalidateQueries({ queryKey: ["/api/donations"] });
      } else {
        toast({
          title: t("error"),
          description: `Import completed with errors. ${result.successCount}/${result.totalRecords} records imported.`,
          variant: "destructive",
        });
      }
    },
    onError: (error) => {
      console.error('Import error:', error);
      toast({
        title: t("error"),
        description: "Failed to import data. Please check the file format and try again.",
        variant: "destructive",
      });
    },
  });

  const handleFileSelect = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      // Validate file type - accept CSV and Excel files
      const validExtensions = ['.csv', '.xlsx', '.xls'];
      const isValidFile = validExtensions.some(ext => file.name.toLowerCase().endsWith(ext));

      if (!isValidFile) {
        toast({
          title: t("error"),
          description: "Please select a CSV (.csv) or Excel (.xlsx, .xls) file",
          variant: "destructive",
        });
        return;
      }
      setSelectedFile(file);
      setImportResult(null);
    }
  };

  const handleImport = () => {
    if (!selectedFile) {
      toast({
        title: t("error"),
        description: "Please select a file first",
        variant: "destructive",
      });
      return;
    }

    importMutation.mutate(selectedFile);
  };

  const downloadTemplate = (format: 'csv' | 'excel' = 'csv') => {
    const headers = [
      'S.No',
      'Receipt No',
      'Name',
      'Community',
      'Location',
      'Address',
      'Phone',
      'Amount',
      'Payment Mode',
      'Inscription',
      'Date'
    ];

    const sampleData = [
      [1, 'R001', 'John Doe', 'payiran', 'Chennai', '123 Temple Street', '9876543210', 1000, 'cash', 'No', '30/06/2025'],
      [2, 'R002', 'Jane Smith', 'chozhan', 'Madurai', '456 Anna Nagar', '8765432109', 2000, 'upi', 'Yes', '29/06/2025'],
      [3, 'R003', 'Ram Kumar', 'any', 'Trichy', '789 Gandhi Road', '9123456789', 500, 'card', 'No', '28/06/2025']
    ];

    if (format === 'excel') {
      // Create Excel file
      const worksheet = XLSX.utils.aoa_to_sheet([headers, ...sampleData]);
      const workbook = XLSX.utils.book_new();
      XLSX.utils.book_append_sheet(workbook, worksheet, 'Donations');

      // Download Excel file
      XLSX.writeFile(workbook, 'donation_import_template.xlsx');
    } else {
      // Create CSV file
      const csvContent = [
        headers.join(','),
        ...sampleData.map(row => 
          row.map(cell => 
            typeof cell === 'string' && cell.includes(',') 
              ? `"${cell}"` 
              : cell
          ).join(',')
        )
      ].join('\n');

      const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8' });
      const url = window.URL.createObjectURL(blob);

      const link = document.createElement('a');
      link.href = url;
      link.download = 'donation_import_template.csv';
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      window.URL.revokeObjectURL(url);
    }
  };

  return (
    <div className="max-w-4xl mx-auto space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">
            {t("importData")}
          </h1>
          <p className="text-gray-600 mt-2">
            Import donation data from CSV or Excel files. Download the template below for the correct format.
          </p>

          {/* Instructions Card */}
          <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 mt-4">
            <h3 className="font-medium text-blue-900 mb-2">File Format Requirements:</h3>
            <ul className="text-sm text-blue-800 space-y-1">
              <li>â€¢ <strong>Required fields:</strong> Name, Phone, Amount</li>
              <li>â€¢ <strong>Phone:</strong> Must be exactly 10 digits</li>
              <li>â€¢ <strong>Amount:</strong> Numbers only (no currency symbols)</li>
              <li>â€¢ <strong>Community:</strong> any, payiran, chozhan, pandiyan, othaalan, vizhiyan, aadai, aavan, odhaalan, semban</li>
              <li>â€¢ <strong>Payment Mode:</strong> cash, card, upi, bankTransfer, cheque</li>
              <li>â€¢ <strong>Inscription:</strong> Yes/No</li>
              <li>â€¢ <strong>Date format:</strong> DD/MM/YYYY (e.g., 30/06/2025) or DD-MM-YYYY (e.g., 30-06-2025)</li>
            </ul>
          </div>
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Upload Section */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center space-x-2">
              <Upload className="h-5 w-5" />
              <span>Upload File</span>
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="border-2 border-dashed border-gray-300 rounded-lg p-6 text-center">
              <input
                type="file"
                accept=".csv,.xlsx,.xls"
                onChange={handleFileSelect}
                className="hidden"
                id="file-upload"
              />
              <label
                htmlFor="file-upload"
                className="cursor-pointer flex flex-col items-center space-y-2"
              >
                <FileText className="h-12 w-12 text-gray-400" />
                <span className="text-sm text-gray-600">
                  Click to select CSV or Excel file or drag and drop
                </span>
              </label>
            </div>

            {selectedFile && (
              <div className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                <div className="flex items-center space-x-2">
                  <FileText className="h-4 w-4 text-gray-500" />
                  <span className="text-sm font-medium">{selectedFile.name}</span>
                </div>
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => setSelectedFile(null)}
                >
                  Remove
                </Button>
              </div>
            )}

            <Button
              onClick={handleImport}
              disabled={!selectedFile || importMutation.isPending}
              className="w-full"
            >
              {importMutation.isPending ? "Importing..." : "Import Data"}
            </Button>
          </CardContent>
        </Card>

        {/* Template Section */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center space-x-2">
              <Download className="h-5 w-5" />
              <span>Download Templates</span>
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <p className="text-sm text-gray-600">
              Download sample templates with the correct format and headers.
            </p>

            <div className="bg-gray-50 p-4 rounded-lg">
              <h4 className="font-medium text-sm mb-2">Required Columns:</h4>
              <ul className="text-xs text-gray-600 space-y-1">
                <li>â€¢ receiptNo (unique receipt number)</li>
                <li>â€¢ name (donor name)</li>
                <li>â€¢ phone (10-digit phone number)</li>
                <li>â€¢ location (donor location)</li>
                <li>â€¢ address (donor address, optional)</li>
                <li>â€¢ community (any, payiran, semban, othaalan, aavan, aadai, vizhiyan)</li>
                <li>â€¢ amount (donation amount as number)</li>
                <li>â€¢ paymentMode (cash, card, upi, bankTransfer, cheque)</li>
                <li>â€¢ inscription (Yes/No)</li>
                <li>â€¢ date (M/D/YYYY format, optional)</li>
              </ul>
            </div>

            <div className="grid grid-cols-2 gap-2">
              <Button
                onClick={(e) => {
                  e.preventDefault();
                  downloadTemplate('csv');
                }}
                variant="outline"
                className="flex-1"
              >
                <Download className="h-4 w-4 mr-2" />
                CSV Template
              </Button>
              <Button
                onClick={(e) => {
                  e.preventDefault();
                  downloadTemplate('excel');
                }}
                variant="outline"
                className="flex-1"
              >
                <Download className="h-4 w-4 mr-2" />
                Excel Template
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Import Results */}
      {importResult && (
        <Card>
          <CardHeader>
