import { createServer } from "http";
import { registerRoutes } from "./routes";
import path from "path";

const app = express();

// Performance optimizations for fast loading
app.use(compression()); // Enable gzip compression
app.use(express.json({ limit: '1mb' })); // Optimize JSON parsing

// Cache static assets for 24 hours for fast loading
app.use((req, res, next) => {
  if (req.url.match(/\.(js|css|png|jpg|jpeg|gif|svg|ico|woff|woff2)$/)) {
    res.setHeader('Cache-Control', 'public, max-age=86400');
  }
  // Enable fast response headers
  res.setHeader('X-Powered-By', 'MERN-Stack-Express');
  next();
});
app.use(express.urlencoded({ extended: false }));

// Session configuration with correct import
app.use((session as any)({
  secret: process.env.SESSION_SECRET || 'temple-donation-secret-key-change-in-production',
  resave: false,
  saveUninitialized: false,
  cookie: {
    secure: false, // Set to true in production with HTTPS
    httpOnly: true,
    maxAge: 24 * 60 * 60 * 1000 // 24 hours
  }
}));

// Serve PWA static files
app.use(express.static('public'));

// Simple logging middleware
app.use((req, res, next) => {
  const start = Date.now();
  const logPath = req.path;

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (logPath.startsWith("/api")) {
      const logLine = `${req.method} ${logPath} ${res.statusCode} in ${duration}ms`;
      console.log(`${new Date().toLocaleTimeString()} [express] âœ“ ${logLine}`);
    }
  });

  next();
});

(async () => {
  // Register routes
  registerRoutes(app);

  // Serve built frontend files
  const __dirname = path.resolve();
  app.use(express.static(path.join(__dirname, 'dist')));

  // SPA fallback route
  app.get('*', (req, res) => {
    if (!req.path.startsWith('/api')) {
      res.sendFile(path.join(__dirname, 'dist', 'index.html'));
    } else {
      res.status(404).json({ error: 'API endpoint not found' });
    }
  });

  const PORT = parseInt(process.env.PORT || "5000", 10);

  const server = createServer(app);

  server.listen(PORT, "0.0.0.0", () => {
    console.log(`${new Date().toLocaleTimeString()} [express] âœ“ Server running on http://localhost:${PORT}`);
