    console.log(`${new Date().toLocaleTimeString()} [express] âœ“ PostgreSQL database connected`);
    console.log(`${new Date().toLocaleTimeString()} [express] âœ“ Temple Donation Management System ready`);
  });
})().catch(console.error);

